+ macOS                if (e.keyCode == 83 && (navigator.platform.match("Mac") ? e.metaKey : e.ctrlKey)) {                    disabledEvent(e);                }                // "U" key                if (e.ctrlKey && e.keyCode == 85) {                    disabledEvent(e);                }                // "F12" key                if (event.keyCode == 123) {                    disabledEvent(e);                }            }, false);            function disabledEvent(e) {                if (e.stopPropagation) {                    e.stopPropagation();                } else if (window.event) {                    window.event.cancelBubble = true;                }                e.preventDefault();                return false;            }        };    </script>    <script>        $(document).ready(function () {            $(window).scroll(function () {                if ($(this).scrollTop() > 100) {                    $('a[href="#top"]').fadeIn();                } else {                    $('a[href="#top"]').fadeOut();                }            });            $('a[href="#top"]').click(function () {                $('html, body').animate({scrollTop: 0}, 800);                return false;            });        });    </script>    <script>        $(document).ready(function () {            $(document).on('click', '.view_data', function () {                var user_id = $(this).attr("id");                //alert(user_id);                //Verificar se há valor na variável "user_id".                if (user_id !== '') {                    var dados = {                        user_id: user_id                    };                    $.post('ajax/produtom.php', dados, function (retorna) {                        //Carregar o conteúdo para o usuário                        $("#visul_usuario").html(retorna);                        $('#visulUsuarioModal').modal('show');  